import React, { useState } from 'react';


import {INTAKES, editIntake} from '../../shared/IntakeFunctions'

import './Intake.css'


const Intake = props => {
    const [editingIntake, setEditingIntake] = useState(false);
    const edit = () => {
        setEditingIntake(true);
    };

    const cancelEdit = () => {
        setEditingIntake(false);
    };

    const editIntakeButton = () => {
        setEditingIntake(false);
       // document.getElementById("myText").value
        editIntake();
    };

    if (!props.program) {
        return (
            <tr>
                <td scope="row" className="noBorderCell centreVertical">Toma {props.number}</td>
                <td className="noBorderCell">{props.date}</td>
                <td className="noBorderCell">{props.hour}</td>
                <td className="noBorderCell">{props.med}</td>
            </tr>
        )
    }
    if (props.program) {
        if (!editingIntake){
        return (
            <tr>
                <td scope="row" className="noBorderCell centreVertical">Toma {props.number}</td>
                <td className="noBorderCell centreVertical">{props.date}</td>
                <td className="noBorderCell centreVertical">{props.hour}</td>
                <td className="noBorderCell centreVertical">{props.med}</td>
                <td>
                    <button name="del0" className='btn btn-primary glyphicon glyphicon-pencil row-remove' onClick={edit}></button>
                </td>
            </tr>
        )
    }
    if (editingIntake){
        return (
            <tr>
                <td scope="row" className="noBorderCell centreVertical">Toma {props.number}</td>
                <td className="noBorderCell"><input id={"date-"+props.number} type="date" placeholder='Fecha' className="form-control" /></td>
                <td className="noBorderCell"><input id={"hour-"+props.number} type="time" placeholder='Hora' className="form-control" /></td>
                <td className="noBorderCell centreVertical"><input id={"Med-"+props.number} type="text" placeholder="Medicamento" className="form-control" /> </td>
                <td data-name="del">
                    <button name="del0" className='btn btn-success glyphicon glyphicon-ok row-remove editButton' onClick={editIntakeButton}></button>
                    <button name="del0" className='btn btn-danger glyphicon glyphicon-remove row-remove' onClick={cancelEdit}></button>
                </td>
            </tr>
        )
    }
    }
}

export default Intake
