import React, { useState } from 'react';
import './Intake.css'

const Intake = props => {
    const [editingIntake, setEditingIntake] = useState(false);
    const editIntake = () => {
        setEditingIntake(true);
    };

    const cancelEdit = () => {
        setEditingIntake(false);
    };
    if (!props.program) {
        return (
            <tr>
                <td scope="row" className="noBorderCell">Toma {props.number}</td>
                <td className="noBorderCell">{props.date}</td>
                <td className="noBorderCell">{props.hour}</td>
                <td className="noBorderCell">{props.med}</td>
            </tr>
        )
    }
    if (props.program) {
        return (
            <tr>
                <td scope="row" className="noBorderCell centreVertical">Toma {props.number}</td>
                <td className="noBorderCell"><input type="date" placeholder='Fecha' className="form-control" /></td>
                <td className="noBorderCell"><input type="time" placeholder='Hora' className="form-control" /></td>
                <td className="noBorderCell centreVertical">{props.med}</td>
                <td data-name="del">
                    <button name="del0" className='btn btn-success glyphicon glyphicon-ok row-remove editButton'></button>
                    <button name="del0" className='btn btn-primary glyphicon glyphicon-pencil row-remove editButton'></button>
                    <button name="del0" className='btn btn-danger glyphicon glyphicon-remove row-remove'></button>
                </td>
            </tr>
        )
    }
}

export default Intake
