import React, { useState } from 'react';


import {INTAKES, editIntake} from '../../shared/IntakeFunctions'

import './Intake.css'


const Intake = props => {
    const [editingIntake, setEditingIntake] = useState(false);
    const [currentIntake, setCurrentIntake] = useState({id:props.number, number: props.number, date: props.date, hour: props.hour, med: props.med });
    const edit = () => {
        setEditingIntake(true);
    };

    const cancelEdit = () => {
        setEditingIntake(false);
    };

    const editIntakeButton = () => {
        setEditingIntake(false);
        const newDate = document.getElementById("date-"+props.number).value;
        const newHour = document.getElementById("hour-"+props.number).value;
        const newMed = document.getElementById("med-"+props.number).value;
        /* document.getElementById("dateStat-"+props.number).value = newDate;
        document.getElementById("hourStat-"+props.number).value = newHour;
        document.getElementById("medStat-"+props.number).value = newMed; */
        const newIntake = {id:props.number, number: props.number, date: newDate, hour: newHour, med: newMed }
        console.log(newIntake)
        editIntake(newIntake);
    };

    if (!props.program) {
        return (
            <tr>
                <td scope="row" className="noBorderCell centreVertical">Toma {currentIntake.number}</td>
                <td className="noBorderCell">{currentIntake.date}</td>
                <td className="noBorderCell">{currentIntake.hour}</td>
                <td className="noBorderCell">{currentIntake.med}</td>
            </tr>
        )
    }
    if (props.program) {
        if (!editingIntake){
        return (
            <tr>
                <td scope="row" className="noBorderCell centreVertical">Toma {currentIntake.number}</td>
                <td id={"dateStat-"+props.number} className="noBorderCell centreVertical">{currentIntake.date}</td>
                <td id={"hourStat-"+props.number} className="noBorderCell centreVertical">{currentIntake.hour}</td>
                <td id={"medStat-"+props.number} className="noBorderCell centreVertical">{currentIntake.med}</td>
                <td>
                    <button name="del0" className='btn btn-primary glyphicon glyphicon-pencil row-remove' onClick={edit}></button>
                </td>
            </tr>
        )
    }
    if (editingIntake){
        return (
            <tr>
                <td scope="row" className="noBorderCell centreVertical">Toma {props.number}</td>
                <td className="noBorderCell"><input id={"date-"+props.number} type="date" placeholder='Fecha' className="form-control" /></td>
                <td className="noBorderCell"><input id={"hour-"+props.number} type="time" placeholder='Hora' className="form-control" /></td>
                <td className="noBorderCell centreVertical"><input id={"med-"+props.number} type="text" placeholder="Medicamento" className="form-control" /> </td>
                <td data-name="del">
                    <button name="del0" className='btn btn-success glyphicon glyphicon-ok row-remove editButton' onClick={editIntakeButton}></button>
                    <button name="del0" className='btn btn-danger glyphicon glyphicon-remove row-remove' onClick={cancelEdit}></button>
                </td>
            </tr>
        )
    }
    }
}

export default Intake
